// MONTANDO O GLOSSÁRIO

function montar_glossario_je() {

	for (x in termos) {

		termo = '<div class="card-header">' +
			'<h3 data-toggle="collapse" data-target="#' + termos[x].sigla + '">' +
			termos[x].titulo +
			'</h3>' +
			'</div>' +
			'<div id="' + termos[x].sigla + '" class="collapse">' +
			'<div class="card-body">' +
			termos[x].resposta +
			'</div>' +
			'</div>';


		str1 = "#estrutura_";
		str2 = ".estrutura_";

		$(sigla = str1.concat(termos[x].sigla.toLowerCase())).prepend(termo);

		str1 = "#estrutura_";

		$(sigla2 = str2.concat(termos[x].sigla.toLowerCase())).prepend(termo);

	}
}

montar_glossario_je();

function mostrar_glossario_letra(letra) {
	$('.je-glossario_perguntas .card').hide();
	$('.glossario-' + letra).show();
}

